    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String adi = txt_name.getText().trim();
        String soyadi = txt_surname.getText().trim();
        String telefon = txt_tel.getText().trim();
        String adres = txt_address.getText().trim();

        if (adi.equals("") || soyadi.equals("") || telefon.equals("") || adres.equals("")) {
            JOptionPane.showMessageDialog(rootPane, "Lütfen Tüm Alanları Doldurunuz!");
            txt_name.requestFocus();
        } else {
            DB db = new DB();
            int status = db.müsteriEkleme(adi, soyadi, telefon, adres);
            db.close();
            if (status > 0) {
                txt_name.setText("");
                txt_surname.setText("");
                txt_tel.setText("");
                txt_name.requestFocus();     
                DB.customer.setId(0);
            } else if (status == -1) {
                JOptionPane.showMessageDialog(rootPane, "Telefon Numarası Zaten Kullanılıyor!");
            }
        }       
        Giris giris = new Giris();
        giris.setVisible(true);
        dispose();
    }//GEN-LAST:event_jButton1ActionPerformed

