    private void txt_telKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_telKeyTyped
        char key = evt.getKeyChar();
        if(!Character.isDigit(key)) {
            evt.consume();
        } 
    }//GEN-LAST:event_txt_telKeyTyped

